
$(document).ready(()=>{var sections=getSectionNamesAndHeights()
var lastLink=$('nav li').first()
$('a[href*="#"]').not('[href="#"]').not('[href="#0"]').click(function(event){if(location.pathname.replace(/^\//,'')==this.pathname.replace(/^\//,'')&&location.hostname==this.hostname){var target=$(this.hash);target=target.length?target:$('[name='+this.hash.slice(1)+']');if(target.length){event.preventDefault();$('html, body').animate({scrollTop:target.offset().top},600,function(){var $target=$(target);$target.focus();if($target.is(":focus")){return false;}else{$target.attr('tabindex','-1');$target.focus();};});}}
lastLink.removeClass('active')
$(this).parents('li').addClass('active')
lastLink=$(this).parents('li')});$(window).scroll((e)=>{let currentPos=$(window).scrollTop()
var sectionNames=Object.keys(sections)
var currentSection=sectionNames[0]
for(var i=1;i<sectionNames.length;i++){if(sections[sectionNames[i]]>currentPos-1){currentSection='#'+sectionNames[i-1]
$('.active').toggleClass('active')
$('a[href="'+currentSection+'"]').parent('li').toggleClass('active')
break;}
currentSection='#'+sectionNames[sectionNames.length-1]
$('.active').toggleClass('active')
$('a[href="'+currentSection+'"]').parent('li').toggleClass('active')}})
function getSectionNamesAndHeights(){let data={}
data['landing-container']=0
$('section').each(function(){data[this.id]=this.offsetTop})
return data}})